def strong_password(password):
    if 6 <= len(password) <= 20 and any(c.islower() for c in password) and any(c.isupper() for c in password) and any(c.isdigit() for c in password) and not any(password[i] == password[i+1] == password[i+2] for i in range(len(password)-2)):
        return 0
    else:
        steps = 0
        if len(password) < 6:
            steps += 6 - len(password)
        elif len(password) > 20:
            steps += len(password) - 20
        if not any(c.islower() for c in password):
            steps += 1
        if not any(c.isupper() for c in password):
            steps += 1
        if not any(c.isdigit() for c in password):
            steps += 1
        for i in range(len(password)-2):
            if password[i] == password[i+1] == password[i+2]:
                steps += 1
        return steps

print(strong_password("Baaba0"))  # Output: 0
print(strong_password("Baaabb0"))  # Output: 1
print(strong_password("Password"))  # Output: 2
print(strong_password("password"))  # Output: 3
print(strong_password("PASSWORD"))  # Output: 3
print(strong_password("abc123"))  # Output: 3
print(strong_password("aB1"))  # Output: 3
print(strong_password(""))

